                    function addMarker(latlng, marker_title) {
                      markers.push(new google.maps.Marker({
                        position: latlng,
                        map: map,
                        title: marker_title,
                        //icon:[enter image location here]
                      }));
                    }

                    google.maps.event.addDomListener(window, 'load', initialize);
